sql.where:
			a.del_flag = 0
			and a.id = #{id}
			and a.name = #{name}
			and a.comments = #{comments}
			and a.class_name = #{className}

			and a.create_by = #{createBy.id}
			
				and a.create_date BETWEEN #{createDateBegin} AND #{createDateEnd}
			
			and a.update_by = #{updateBy.id}
			
				and a.update_date BETWEEN #{updateDateBegin} AND #{updateDateEnd}

			a.del_flag = 0
			and a.id = #{id}
			
				and a.name LIKE
				'%'||#{name}||'%'
				CONCAT('%', #{name}, '%')
			
			
				and a.comments LIKE
				'%'||#{comments}||'%'
				CONCAT('%', #{comments}, '%')
			
			and a.class_name = #{className}

			and a.create_by = #{createBy.id}
			
				and a.create_date BETWEEN #{createDateBegin} AND #{createDateEnd}
			
			and a.update_by = #{updateBy.id}
			
				and a.update_date BETWEEN #{updateDateBegin} AND #{updateDateEnd}

mapper.update:
update gen_table a

	a.name = #{name},
	a.comments = #{comments},
	a.class_name = #{className}

where a.id = #{id,jdbcType=VARCHAR}

mapper.select:
select

from gen_table a



	order by ${page.order}
	
		${page.orderDir}

