sql.where:
a.del_flag = 0
and a.id = #{id}
and a.label = #{label}
and a.value = #{value}
and a.type = #{type}
and a.sort = #{sort}
and a.description = #{description}

and a.create_by = #{createBy.id}

	and a.create_date BETWEEN #{createDateBegin} AND #{createDateEnd}

and a.update_by = #{updateBy.id}

	and a.update_date BETWEEN #{updateDateBegin} AND #{updateDateEnd}

a.del_flag = 0
and a.id = #{id}

	and a.label LIKE
	'%'||#{label}||'%'
	CONCAT('%', #{label}, '%')


	and a.value LIKE
	'%'||#{value}||'%'
	CONCAT('%', #{value}, '%')

and a.type = #{type}
and a.sort = #{sort}

	and a.description LIKE
	'%'||#{description}||'%'
	CONCAT('%', #{description}, '%')


and a.create_by = #{createBy.id}

	and a.create_date BETWEEN #{createDateBegin} AND #{createDateEnd}

and a.update_by = #{updateBy.id}

	and a.update_date BETWEEN #{updateDateBegin} AND #{updateDateEnd}

mapper.insert:
insert into sys_dict(
id, label, value, type, sort, description, create_by, create_date, update_by, update_date, del_flag
)values(
#{id}, #{label}, #{value}, #{type}, #{sort}, #{description}, #{createBy.id}, #{createDate}, #{updateBy.id}, #{updateDate}, #{delFlag}
)

mapper.update:
