sql.where:
			a.del_flag = 0
			and a.id = #{id}
			and a.name = #{name}
			and a.href = #{href}

			and a.create_by = #{createBy.id}
			
				and a.create_date BETWEEN #{createDateBegin} AND #{createDateEnd}
			
			and a.update_by = #{updateBy.id}
			
				and a.update_date BETWEEN #{updateDateBegin} AND #{updateDateEnd}

			a.del_flag = 0
			and a.id = #{id}
			and a.name = #{name}
			and a.href = #{href}

			and a.create_by = #{createBy.id}
			
				and a.create_date BETWEEN #{createDateBegin} AND #{createDateEnd}
			
			and a.update_by = #{updateBy.id}
			
				and a.update_date BETWEEN #{updateDateBegin} AND #{updateDateEnd}

mapper.insert:
insert into sys_menu(
id, name, href, create_by, create_date, update_by, update_date, del_flag
)values(
#{id}, #{name}, #{href}, #{createBy.id}, #{createDate}, #{updateBy.id}, #{updateDate}, #{delFlag}
)

mapper.update:
update sys_menu a
a.name = #{name,jdbcType=VARCHAR}, a.href = #{href,jdbcType=VARCHAR},
where a.id = #{id,jdbcType=VARCHAR}

mapper.select:
