sql.where:
			a.del_flag = 0
			and a.id = #{id}
			and a.name = #{name}
			and a.href = #{href}
			and a.permission_str = #{permissionStr}
			and a.type = #{type}

			and a.parent_id = #{parent.id}
			and a.parent_ids = #{parentIds}

			and a.create_by = #{createBy.id}
			
				and a.create_date BETWEEN #{createDateBegin} AND #{createDateEnd}
			
			and a.update_by = #{updateBy.id}
			
				and a.update_date BETWEEN #{updateDateBegin} AND #{updateDateEnd}

			a.del_flag = 0
			and a.id = #{id}
			
				and a.name LIKE
				'%'||#{name}||'%'
				CONCAT('%', #{name}, '%')
			
			and a.href = #{href}
			
				and a.permission_str LIKE
				'%'||#{permissionStr}||'%'
				CONCAT('%', #{permissionStr}, '%')
			
			and a.type = #{type}

			and a.parent_id = #{parent.id}
			and a.parent_ids = #{parentIds}

			and a.create_by = #{createBy.id}
			
				and a.create_date BETWEEN #{createDateBegin} AND #{createDateEnd}
			
			and a.update_by = #{updateBy.id}
			
				and a.update_date BETWEEN #{updateDateBegin} AND #{updateDateEnd}

mapper.insert:
insert into sys_permission(
id, name, href, permission_str, type, parent_id, parent_ids, create_by, create_date, update_by, update_date, del_flag
)values(
#{id}, #{name}, #{href}, #{permissionStr}, #{type}, #{parent.id}, #{parentIds}, #{createBy.id}, #{createDate}, #{updateBy.id}, #{updateDate}, #{delFlag}
)

mapper.update:
update sys_permission a SET a.del_flag = '1'
where a.id in

	#{item}

update sys_permission a
a.name = #{name}, a.href = #{href}, a.permission_str = #{permissionStr}, a.type = #{type}, a.parent_id = #{parent.id}, a.parent_ids = #{parentIds}, a.update_by = #{updateBy.id}, a.update_date = #{updateDate},
where a.id = #{id,jdbcType=VARCHAR}

mapper.select:
select

from sys_permission a



	order by ${page.order}
	
