sql.where:
			a.del_flag = 0
			and a.id = #{id}
			and a.name = #{name}
			and a.permission_str = #{permissionStr}

			and a.create_by = #{createBy.id}
			
				and a.create_date BETWEEN #{createDateBegin} AND #{createDateEnd}
			
			and a.update_by = #{updateBy.id}
			
				and a.update_date BETWEEN #{updateDateBegin} AND #{updateDateEnd}

			a.del_flag = 0
			and a.id = #{id}
			
				and a.name LIKE
				'%'||#{name}||'%'
				CONCAT('%', #{name}, '%')
			
			and a.permission_str = #{permissionStr}

			and a.create_by = #{createBy.id}
			
				and a.create_date BETWEEN #{createDateBegin} AND #{createDateEnd}
			
			and a.update_by = #{updateBy.id}
			
				and a.update_date BETWEEN #{updateDateBegin} AND #{updateDateEnd}

mapper.insert:
insert into sys_permission(
id, name, permission_str, create_by, create_date, update_by, update_date, del_flag
)values(
#{id}, #{name}, #{permissionStr}, #{createBy.id}, #{createDate}, #{updateBy.id}, #{updateDate}, #{delFlag}
)

mapper.update:
update sys_permission a
a.name = #{name,jdbcType=VARCHAR}, a.permission_str = #{permissionStr,jdbcType=VARCHAR},
where a.id = #{id,jdbcType=VARCHAR}

mapper.select:
select

from sys_permission a



	order by ${page.order}
	
		${page.orderDir}

