sql.where:
			a.del_flag = 0
			and a.id = #{id}
			and a.name = #{name}
			and a.role_str = #{roleStr}
			and a.description = #{description}

			and a.create_by = #{createBy.id}
			
				and a.create_date BETWEEN #{createDateBegin} AND #{createDateEnd}
			
			and a.update_by = #{updateBy.id}
			
				and a.update_date BETWEEN #{updateDateBegin} AND #{updateDateEnd}

			a.del_flag = 0
			and a.id = #{id}
			
				and a.name LIKE
				'%'||#{name}||'%'
				CONCAT('%', #{name}, '%')
			
			
				and a.role_str LIKE
				'%'||#{roleStr}||'%'
				CONCAT('%', #{roleStr}, '%')
			
			
				and a.description LIKE
				'%'||#{description}||'%'
				CONCAT('%', #{description}, '%')
			

			and a.create_by = #{createBy.id}
			
				and a.create_date BETWEEN #{createDateBegin} AND #{createDateEnd}
			
			and a.update_by = #{updateBy.id}
			
				and a.update_date BETWEEN #{updateDateBegin} AND #{updateDateEnd}

mapper.insert:
insert into sys_role(
id, name, role_str, description, create_by, create_date, update_by, update_date, del_flag
)values(
#{id}, #{name}, #{roleStr}, #{description}, #{createBy.id}, #{createDate}, #{updateBy.id}, #{updateDate}, #{delFlag}
)

mapper.update:
update sys_role a SET a.del_flag = '1'
where a.id in

	#{item}

update sys_role a
a.name = #{name,jdbcType=VARCHAR}, a.role_str = #{roleStr,jdbcType=VARCHAR}, a.description = #{description,jdbcType=VARCHAR}, a.update_by = #{updateBy.id}, a.update_date = #{updateDate},
where a.id = #{id,jdbcType=VARCHAR}

mapper.select:
select

from sys_role a



	order by ${page.order}
	
		${page.orderDir}

